import { render } from 'react-dom';
//import react library 
//react hooks are functions thatallow you to hook into the react state and also into the react cycle
import './App.css';
const apiKey = '273db221-969c-4e89-9549-498ce6999f89';
const url ='https://api.thecatapi.com/v1/breeds';


function getRandomInt(max) {
  return Math.floor(Math.random() * max);
}

function App(){ //define a function 
  const [catUrl, setCatUrl] = useState('');
  const [catInfo, setinfoUrl] = useState('');
  useEffect(() => {

    getPic();
  }, [])



  const getPic = () => {



    fetch(url, { 
       headers: {
         'x-api-key': apiKey}}
      )
    .then((res) => res.json())
    .then((cats) => {
      console.log('Cats: ', cats);
      const newCat = cats[{generateRandomNumber}].image.url;
      const newInfo = cats[{generateRandomNumber}].description;

      setCatUrl(newCat);
      setinfoUrl(newInfo);
    })
    .catch((error) => {
      console.log('error', error);
  });


}





return(
  <div className="app">
  <h1> Bilal's Cat App</h1>
  <div>  
  <p1><img src={catUrl} height="200" width="200" border="1px"></img></p1>
  <p name ="t"> Cat Facts </p>
  <br></br>
  <p> {catInfo} </p>
  </div>



  <button onClick ={getPic}>Click for new image</button>
  </div>
  //returned html inside a function
);
}

export default App; // exports the app
